
public class Arithmeticoperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=4, y=20;
		System.out.println(x);
		System.out.println(y);
		System.out.println(x+y);
		System.out.println(x-y);
		System.out.println(x*y);
		System.out.println(x/y);
		System.out.println(x%y);
		
		
		

	}

}
