
public class incdecoperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=10;
		System.out.println("x: "+(x));
		x++;
		System.out.println("x after incrementing once: "+(x));
		x++;
		System.out.println("x after incrementing twice: "+(x));
		x++;
		System.out.println("x after incrementing thrice: "+(x));
		x--;
		System.out.println("x after one decrement: "+(x));
		
	}

}
